export const loggerMixin = {
  data() {
    return {
      logMessages: [],
    };
  },
  methods: {
    log(message) {
      this.logMessages.push(message);
      console.log(message);
    },
  },
  created() {
    this.log('Mixin \u7684 created \u94A9\u5B50\u89E6\u53D1');
  },
};
