const MyPlugin = {
  install(Vue, options = {}) {
    // 1. \u6DFB\u52A0\u5168\u5C40\u65B9\u6CD5/\u5C5E\u6027
    Vue.myGlobalMethod = () => {
      console.log('\u5168\u5C40\u65B9\u6CD5\u88AB\u8C03\u7528');
    };

    // 2. \u6DFB\u52A0\u5168\u5C40\u6307\u4EE4
    Vue.directive('focus', {
      inserted: (el) => el.focus(),
    });

    // 3. \u6DFB\u52A0\u5B9E\u4F8B\u65B9\u6CD5
    Vue.prototype.$showToast = (message) => {
      alert(message || options.defaultMessage || '\u9ED8\u8BA4\u63D0\u793A');
    };

    // 4. \u5168\u5C40\u6DF7\u5165
    Vue.mixin({
      created() {
        console.log('\u63D2\u4EF6\u6CE8\u5165\u7684 created \u94A9\u5B50');
      },
    });
  },
};

export default MyPlugin;
`,paraId:56,tocIndex:14},{value:"\u5B89\u88C5\u63D2\u4EF6",paraId:57,tocIndex:14},{value:"\u901A\u8FC7 Vue.use() \u5B89\u88C5\u63D2\u4EF6\uFF0C\u53EF\u4F20\u5165\u914D\u7F6E\u9009\u9879\u3002",paraId:58,tocIndex:14},{value:`// main.js
