// \u901A\u8FC7 Object.defineProperty \u76D1\u542C\u5C5E\u6027 "a"
Object.defineProperty(obj, 'a', {
  get() {
    console.log('\u8BFB\u53D6\u5C5E\u6027 a');
    return this._a;
  },
  set(value) {
    console.log('\u8BBE\u7F6E\u5C5E\u6027 a');
    this._a = value;
  },
});

// \u4FEE\u6539\u5DF2\u6709\u5C5E\u6027 a\uFF1A\u53EF\u4EE5\u89E6\u53D1 setter
