obj.b = 3;
console.log(obj.b); // \u63A7\u5236\u53F0\u8F93\u51FA 3\uFF08\u65E0getter\u8DDFsetter\u65E5\u5FD7\uFF09

// \u5220\u9664\u5C5E\u6027 a\uFF1A\u4E0D\u4F1A\u89E6getter\u8DDFsetter\u53D1\u76D1\u542C
