router.beforeEach((to, from, next) => {
  const isPublicRoute = to.matched.some(record => record.meta.isPublic);
  const isLoggedIn = checkAuth(); // \u81EA\u5B9A\u4E49\u9A8C\u8BC1\u65B9\u6CD5

  if (!isPublicRoute && !isLoggedIn) {
    next({ path: '/login', query: { redirect: to.fullPath } });
  } else {
    next();
  }
});

// \u8DEF\u7531\u914D\u7F6E\u6807\u8BB0\u516C\u5F00\u8DEF\u7531
{
  path: '/login',
  component: Login,
  meta: { isPublic: true }
}
