let scrollPositions = {};

router.beforeEach((to, from, next) => {
  scrollPositions[from.path] = window.pageYOffset;
  next();
});

router.afterEach((to, from) => {
  window.scrollTo(0, scrollPositions[to.path] || 0);
});
