export default {
  methods: {
    // \u65B9\u5F0F1\uFF1A\u6570\u7EC4\u5F62\u5F0F\uFF08\u540C\u540D\u6620\u5C04\uFF09
    ...mapActions(['incrementAsync', 'incrementByAsync']),
    // \u8C03\u7528\uFF1Athis.incrementAsync() \u6216 this.incrementByAsync(10)

    // \u65B9\u5F0F2\uFF1A\u5BF9\u8C61\u5F62\u5F0F\uFF08\u522B\u540D\u6620\u5C04\uFF09
    ...mapActions({
      // \u5C06 this.asyncAdd() \u6620\u5C04\u4E3A this.$store.dispatch('incrementAsync')
      asyncAdd: 'incrementAsync',
      // \u5C06 this.asyncAddBy(n) \u6620\u5C04\u4E3A this.$store.dispatch('incrementByAsync', n)
      asyncAddBy: 'incrementByAsync',
    }),
  },
};
`,paraId:70,tocIndex:17},{value:"Module \u662F Vuex \u4E2D\u7EC4\u7EC7\u5927\u578B\u5E94\u7528\u72B6\u6001\u6811\u7684\u6838\u5FC3\u673A\u5236\uFF0C\u5B83\u7684\u6838\u5FC3\u4F5C\u7528\u5305\u62EC\uFF1A",paraId:71,tocIndex:18},{value:"\u4EE3\u7801\u62C6\u5206\uFF1A\u5C06\u590D\u6742\u7684\u72B6\u6001\u903B\u8F91\u6309\u529F\u80FD\u62C6\u5206\u4E3A\u72EC\u7ACB\u6A21\u5757\uFF0C\u907F\u514D\u5355\u4E00 Store \u6587\u4EF6\u81C3\u80BF\u3002",paraId:72,tocIndex:18},{value:"\u547D\u540D\u7A7A\u95F4\u9694\u79BB\uFF1A\u89E3\u51B3\u4E0D\u540C\u6A21\u5757\u95F4\u7684 State/Mutation/Action \u547D\u540D\u51B2\u7A81\u95EE\u9898\u3002",paraId:72,tocIndex:18},{value:"\u590D\u7528\u6027\uFF1A\u53EF\u590D\u7528\u901A\u7528\u6A21\u5757\uFF08\u5982\u7528\u6237\u6A21\u5757\u3001\u5546\u54C1\u6A21\u5757\uFF09\u3002",paraId:72,tocIndex:18},{value:"\u56E2\u961F\u534F\u4F5C\uFF1A\u4E0D\u540C\u5F00\u53D1\u8005\u53EF\u72EC\u7ACB\u7EF4\u62A4\u4E0D\u540C\u6A21\u5757\u3002",paraId:72,tocIndex:18},{value:"\u6BCF\u4E2A\u6A21\u5757\u662F\u4E00\u4E2A\u5305\u542B state\u3001mutations\u3001actions\u3001getters \u7684\u72EC\u7ACB\u5BF9\u8C61",paraId:73,tocIndex:19},{value:`// userModule.js
const userModule = {
  namespaced: true, // \u{1F534} \u5F00\u542F\u547D\u540D\u7A7A\u95F4\uFF08\u5173\u952E\uFF01\uFF09
  state: () => ({
    name: 'Guest',
    token: '',
  }),
  mutations: {
    SET_USER(state, payload) {
      state.name = payload.name;
      state.token = payload.token;
    },
  },
  actions: {
    login({ commit }, credentials) {
      return api.login(credentials).then((user) => {
        commit('SET_USER', user); // \u8C03\u7528\u672C\u6A21\u5757\u7684 Mutation
      });
    },
  },
  getters: {
    isLoggedIn: (state) => !!state.token,
  },
};
